$(window).on("load",(function(){document.querySelectorAll(".collection__item").forEach((e=>e.addEventListener("mouseover",(function(e){$(this.querySelector(".collection__item-hide-list")).slideDown("slow")})))),document.querySelectorAll(".collection__item").forEach((e=>e.addEventListener("mouseleave",(function(e){$(this.querySelector(".collection__item-hide-list")).slideUp("slow")}))))})),document.querySelector(".collection__item-cover")&&document.querySelectorAll(".collection__item-cover").forEach((e=>""==e.textContent))&&(this.style.display="none");