$(window).on("load",(function(){document.querySelectorAll(".collection__item").forEach((e=>e.addEventListener("mouseover",(function(e){$(this.querySelector(".collection__item-hide-list")).slideDown("slow")})))),document.querySelectorAll(".collection__item").forEach((e=>e.addEventListener("mouseleave",(function(e){$(this.querySelector(".collection__item-hide-list")).slideUp("slow")}))))})),document.querySelector(".collection__item-cover")&&""==document.querySelector(".collection__item-cover").textContent&&(document.querySelector(".collection__item-cover").style.display="none");